CREATE TABLE `page_content` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `page` varchar(200) COLLATE utf8_unicode_ci NOT NULL,  `paragraph_index` int(11) NOT NULL,  `text` text COLLATE utf8_unicode_ci NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
CREATE TABLE `settings` (  `setting` varchar(255) COLLATE utf8_unicode_ci NOT NULL,  `value` varchar(255) COLLATE utf8_unicode_ci NOT NULL,  PRIMARY KEY (`setting`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
CREATE TABLE `users` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `username` varchar(100) COLLATE utf8_unicode_ci NOT NULL,  `firstname` varchar(50) COLLATE utf8_unicode_ci NOT NULL,  `lastname` varchar(50) COLLATE utf8_unicode_ci NOT NULL,  `sex` varchar(10) COLLATE utf8_unicode_ci NOT NULL,  `email` varchar(50) COLLATE utf8_unicode_ci NOT NULL,  `password` varchar(100) COLLATE utf8_unicode_ci NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
CREATE TABLE `vorstand` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `darstellung` varchar(50) COLLATE utf8_unicode_ci NOT NULL,  `name` varchar(100) COLLATE utf8_unicode_ci NOT NULL,  `bereich` varchar(255) COLLATE utf8_unicode_ci NOT NULL,  `email` varchar(150) COLLATE utf8_unicode_ci NOT NULL,  `telefon` varchar(20) COLLATE utf8_unicode_ci NOT NULL,  `foto` varchar(255) COLLATE utf8_unicode_ci NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=52 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci ROW_FORMAT=COMPACT;
